mapper.update:
UPDATE personal_objective SET title = #{personalObjective.title}, description = #{personalObjective.description}, meets = #{personalObjective.meets}, exceeds = #{personalObjective.exceeds}, status_id=#{personalObjective.status.id} WHERE uuid = #{personalObjective.uuid};
DELETE FROM objective_link WHERE personal_objective_uuid = #{personalObjective.uuid};
INSERT INTO objective_link (personal_objective_uuid, group_objective_uuid) VALUES (#{personalObjective.uuid}, #{personalObjective.groupObjectiveUuid}) ON CONFLICT (personal_objective_uuid) DO UPDATE SET group_objective_uuid = EXCLUDED.group_objective_uuid where objective_link.group_objective_uuid != EXCLUDED.group_objective_uuid;

mapper.sql:
select coalesce(max(version), 0) as version
from group_objective go
where go.business_unit_uuid = #{businessUnitUuid}
