mapper.select:
SELECT c1.iam_id as "employee_no", c1.uuid as "employee_uuid", c1.first_name as "first_name", c1.last_name as "surname", c1.work_level as "working_level", j.name as "job_title", c2.first_name||' '||c2.last_name as "line_manager", pr.number as "objective_number", pr.status_id as "status", pr.properties::json->'strategic_priority' as "strategic_priority", pr.properties::json->'title' as "objective", pr.properties::json->'how_achieved' as "how_achieved", pr.properties::json->'how_over_achieved' as "how_over_achieved" FROM colleague c1 LEFT JOIN job j ON c1.job_id = j.id LEFT JOIN colleague c2 ON c1.manager_uuid = c2.uuid INNER JOIN pm_colleague_cycle pcc ON c1.uuid = pcc.colleague_uuid INNER JOIN pm_cycle pc ON pcc.cycle_uuid = pc.uuid INNER JOIN pm_timeline_point ptp ON pcc.uuid = ptp.colleague_cycle_uuid INNER JOIN pm_review pr ON ptp.uuid = pr.tl_point_uuid INNER JOIN pm_tl_point_status ptps ON pr.status_id = ptps.id WHERE pr.type_id = 1 AND c1.work_level IN ('WL4', 'WL5') AND pc.type_id = 1
AND EXTRACT(YEAR FROM pc.start_time) = #{year}
AND pr.status_id IN
#{status.id}
