mapper.update:
UPDATE pm_review
SET properties =
#{review.properties, jdbcType=OTHER, typeHandler=com.tesco.pma.dao.utils.jsonb.MapJsonbTypeHandler},
status_id = #{review.status.id}
WHERE tl_point_uuid = #{review.tlPointUuid}
and type_id = #{review.type.id}
and number = #{review.number}

    and status_id in

        #{allowedStatus.id}

mapper.delete:
DELETE
FROM pm_review r
WHERE r.uuid = #{uuid}

    and r.status_id in

        #{allowedStatus.id}

mapper.select:
with change_status_reason as ()

WHERE 1=1

    AND r.tl_point_uuid = #{tlPointUuid}


    AND r.type_id = #{type.id}


    AND r.status_id = #{status.id}


    AND r.number = #{number}

mapper.update:
update pm_review r
SET status_id=#{newStatus.id}
WHERE 1=1

    AND r.tl_point_uuid = #{tlPointUuid}


    AND r.type_id = #{type.id}


    AND r.number = #{number}


    and r.status_id in

        #{prevStatus.id}

mapper.delete:
DELETE
FROM pm_review r
WHERE 1=1

    AND r.tl_point_uuid = #{tlPointUuid}


    AND r.type_id = #{type.id}


    AND r.status_id = #{status.id}


    AND r.number = #{number}


    and r.status_id in

        #{allowedStatus.id}

mapper.update:
update pm_review
set number = number - 1
WHERE tl_point_uuid = #{tlPointUuid}
  and type_id = #{type.id}
  and number >= #{startNumber}
