mapper.update:
UPDATE pm_timeline_point SET start_time = #{tlPoint.startTime}, end_time = #{tlPoint.endTime}, properties = #{tlPoint.properties, jdbcType=OTHER, typeHandler=com.tesco.pma.dao.utils.jsonb.MapJsonbTypeHandler}, status_id = #{tlPoint.status.id}, description = #{tlPoint.description}, element_type_id = #{tlPoint.type, typeHandler=com.tesco.pma.dao.utils.DictionaryItemTypeHandler, javaType=com.tesco.pma.cycle.api.model.PMElementType}, review_count = #{tlPoint.count}, last_updated_time = #{tlPoint.lastUpdatedTime} WHERE colleague_cycle_uuid = #{tlPoint.colleagueCycleUuid} and code = #{tlPoint.code}
and status_id in
#{allowedStatus.id}

mapper.delete:
DELETE
FROM pm_timeline_point tlp
WHERE tlp.uuid = #{uuid}

    and tlp.status_id in

        #{allowedStatus.id}

mapper.select:
select tlp.*, prt.code as review_type_code from pm_timeline_point tlp left join pm_review_type prt on (prt.code = upper(tlp.code)) WHERE 1=1
AND tlp.colleague_cycle_uuid = #{colleagueCycleUuid}
AND upper(tlp.code) = upper(#{code})
AND tlp.status_id = #{status.id}

mapper.update:
update pm_timeline_point tlp
SET status_id=#{newStatus.id}
WHERE 1=1

    AND tlp.colleague_cycle_uuid = #{colleagueCycleUuid}


    AND upper(tlp.code) = upper(#{code})


    and tlp.status_id in

        #{prevStatus.id}

update pm_timeline_point tlp
SET status_id=#{newStatus.id},
last_updated_time=#{lastUpdatedTime}
WHERE 1=1
AND tlp.uuid = #{uuid}

    and tlp.status_id in

        #{prevStatus.id}

mapper.delete:
DELETE
FROM pm_timeline_point tlp
WHERE 1=1

    AND tlp.colleague_cycle_uuid = #{colleagueCycleUuid}


    AND upper(tlp.code) = upper(#{code})


    AND tlp.status_id = #{status.id}


    and tlp.status_id in

        #{allowedStatus.id}
