mapper.select:
SELECT a.id as id, a.name as name, a.type_id as type_id, a.status_id as status_id, a.iam_id as iam_id, a.employee_number as employee_number, a.last_login as last_login, r.id as role_id, r.code as role_code FROM (SELECT * FROM pm_account ORDER BY name
LIMIT #{requestQuery.limit}
OFFSET #{requestQuery.offset}
) a LEFT JOIN pm_account_role ar on a.id = ar.account_id LEFT JOIN pm_role r on ar.role_id = r.id ORDER BY name;

mapper.update:
UPDATE pm_account
SET status_id = #{status, typeHandler=com.tesco.pma.dao.utils.DictionaryItemTypeHandler, javaType=com.tesco.pma.colleague.security.domain.AccountStatus},
    last_login = #{now}
WHERE name = #{name}

UPDATE pm_account
SET status_id = #{status, typeHandler=com.tesco.pma.dao.utils.DictionaryItemTypeHandler, javaType=com.tesco.pma.colleague.security.domain.AccountStatus},
    last_login = #{now}
WHERE name = #{name}

mapper.delete:
DELETE
FROM pm_account_role
WHERE account_id = #{accountId}
  AND role_id = #{roleId}
